import React, {Component} from 'react'
import { CSSTransitionGroup } from 'react-transition-group'
import { Layout, Menu, Icon, Table, Avatar, Divider, Select, Modal, Tooltip, Dropdown } from 'antd'
import { Link } from 'react-router-dom'


const { Header, Sider, Content } = Layout

let dataSource = []

for (let index = 0; index < 100; index++) {
  dataSource.push(
    {
      key: index,
      name: 'Mike',
      age: 32 + index,
      address: '10 Downing Street'
    }
  )
  
}

const columns = [{
  title: 'Name',
  dataIndex: 'name',
  key: 'name',
}, {
  title: 'Age',
  dataIndex: 'age',
  key: 'age',
}, {
  title: 'Address',
  dataIndex: 'address',
  key: 'address',
}]

class Dashboard extends Component{
  constructor(props) {
    super( props )
    
    this.state = {
      collapsed: false,
      itemsPerPage: 5,
      isLogOutModalShowing: false,
      isMobile: false
    }

    this.toggle = this.toggle.bind( this )
    this.handleItemsPerPageChange = this.handleItemsPerPageChange.bind( this )
    this.showLogOutModal = this.showLogOutModal.bind( this )
    this.logOutUser = this.logOutUser.bind( this )

    this.handleViewportChange = this.handleViewportChange.bind( this )
    this.handleClickOutside = this.handleClickOutside.bind( this )
  }

  componentDidMount() {
    this.handleViewportChange()
    window.addEventListener( "resize", this.handleViewportChange )
  }

  componentWillMount() {
    if (localStorage.isLogin != 'true') {
      this.props.history.push( '/' )
    } 
  }

  handleClickOutside( ) {
    if (!this.state.collapsed && this.state.isMobile) {
      this.setState( {
        collapsed: !this.state.collapsed,
        itemsPerPage: this.state.itemsPerPage,
        isLogOutModalShowing: this.state.isLogOutModalShowing,
        isMobile: this.state.isMobile
      } )
    }

  }

  toggle() {
    this.setState( {
      collapsed: !this.state.collapsed,
      itemsPerPage: this.state.itemsPerPage,
      isLogOutModalShowing: this.state.isLogOutModalShowing,
      isMobile: this.state.isMobile
    } )
  }

  handleViewportChange() {
    this.setState( {
      collapsed: window.innerWidth <= 768 || this.state.collapsed,
      itemsPerPage: this.state.itemsPerPage,
      isLogOutModalShowing: this.state.isLogOutModalShowing,
      isMobile: window.innerWidth <= 768
    } )
  }

  handleItemsPerPageChange( change ) {
    this.setState( {
      collapsed: this.state.collapsed,
      itemsPerPage: Number(change),
      isLogOutModalShowing: this.state.isLogOutModalShowing,
      isMobile: this.state.isMobile
    } )

    console.log( change )
  }

  showLogOutModal() {
    this.setState( {
      collapsed: this.state.collapsed,
      itemsPerPage: this.state.itemsPerPage,
      isLogOutModalShowing: !this.state.isLogOutModalShowing,
      isMobile: this.state.isMobile
    } )
  }

  logOutUser() {
    localStorage.removeItem("isLogin")
    this.props.history.push( '/' )
  }

  render() {
    const paginationConfig = {pageSize: this.state.itemsPerPage}
    const collapsed = this.state.isMobile && !this.state.collapsed
    const hoverMenu = (
      <Menu className="user-menu" selectedKeys={[]} onClick={this.showLogOutModal}>
        <Menu.Item disabled><Icon type="user" />Perfil</Menu.Item>
        <Menu.Item disabled><Icon type="setting" />Settings</Menu.Item>
        <Menu.Divider />
        <Menu.Item key="logout"><Icon type="logout" onClick={this.showLogOutModal} />Log Out</Menu.Item>
      </Menu>
    )

    return(
      <Layout className="dashboard-layout">
        <Sider 
          trigger = {null}
          breakpoint = "lg"
          width={256}
          collapsible collapsed={ this.state.collapsed }
          className = { this.state.collapsed ? `sider drawer drawer-hide` : `sider drawer drawer-expanded` }
        >
          
          <div className="logo">
            <img src="data:image/svg+xml;base64," alt="logo" />
            <h1>Ant Design Pro</h1>
          </div>

          <Menu theme="dark" mode="inline" style={{ padding: '16px 0', width: '100%' }} defaultSelectedKeys={['1']}>
            <Menu.Item key="1">
              <Icon type="line-chart" />
              <span>Table</span>
            </Menu.Item>

            <Menu.Item key="2">
              <Link to="/graphics">
                <Icon type="area-chart" />
                <span>Graphics</span>
              </Link>
            </Menu.Item>

            
          </Menu>
        </Sider>

        <Layout onClick={this.handleClickOutside}>
          <Header className="dashboard-header">
            <Icon
              className="trigger"
              type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
              onClick={this.toggle}
            />

           <div className="header-right-menu">
            <Tooltip title="Test">
              <span className="action" rel="noopener noreferrer">
                <Icon type="question-circle-o" />
              </span>
            </Tooltip>

            <Dropdown overlay={hoverMenu}>
              <span className="action account">
                <Avatar size="small" className="avatar" src="https://gw.alipayobjects.com/zos/rmsportal/BiazfanxmamNRoxxVxka.png" />
                <span className="name">Current User</span>
              </span>
            </Dropdown>

            <Modal
              title="Log Out"
              visible={this.state.isLogOutModalShowing}
              onOk={this.logOutUser}
              onCancel={this.showLogOutModal}
            >
              <p>Are you sure?</p>
              
            </Modal>
           </div>
            
          </Header>

          <div className="dashboard-content">
            <h2>Table</h2>
            <Divider/>

            Items per page: <Select onChange={this.handleItemsPerPageChange} value={this.state.itemsPerPage} style={{ width: '10%' }}>
              <Select.Option value="5">5</Select.Option>
              <Select.Option value="10">10</Select.Option>
              <Select.Option value="25">25</Select.Option>
              <Select.Option value="50">50</Select.Option>
              <Select.Option value="75">75</Select.Option>
            </Select>

            <CSSTransitionGroup
              transitionName="example"
              transitionAppear={true}
              transitionEnterTimeout={500}
              transitionLeaveTimeout={300}
            > 
              <Table className="data-table" dataSource={dataSource} columns={columns} pagination={ paginationConfig } />
            </CSSTransitionGroup>
            
          </div>
        </Layout>

      </Layout>
    )
  }
}

module.exports = Dashboard